package july.assignment;

public class CheckTheGivenNumberIsEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//declare a variable and assign the value to 20
		int a =20;
		// if the number is divisible by 2, then its even number else its an odd number
		if(a%2==0) {
			System.out.println("The number is even");
		}
		else {
			System.out.println("The number is odd");
		}

	}

}
