package july.assignment;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Iterating the values from 1 to 5 in ascending order
		for(int i=0;i<=5;i++) {
			System.out.println(i);
		}
		//Iterating the values from 5 to 1 in descending order
		for(int i=5;i>=1;i--) {
			System.out.println(i);
		}

	}

}
